import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Settings from './Settings';
import Canvas from './Canvas';
import ErrorModal from './ErrorModal';
import '../App.css';

const App = () => {
  const [config, setConfig] = useState(null);  // Initialize config as null
  const [pattern, setPattern] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('/config.json')
      .then(response => setConfig(response.data))
      .catch(error => setError(error.message));
  }, []);

  const generatePattern = (settings) => {
    try {
      // Add logic to generate pattern based on settings
      // For example, use a canvas to draw the text pattern
      // You can refer to the previous JavaScript code to generate the pattern
      // For simplicity, setting a dummy pattern
      setPattern("data:image/jpeg;base64,dummypattern");
    } catch (err) {
      setError(err.message);
    }
  };

  // Render a loading state if config is null
  if (config === null) {
    return <div>Loading...</div>;
  }

  return (
    <div className="app-container">
      <div className="header">
        <h1>Text Pattern Generator</h1>
        <p>Generate beautiful text patterns</p>
      </div>
      <div className="content">
        <div className="settings-container">
          <Settings config={config} generatePattern={generatePattern} />
        </div>
        <div className="canvas-container">
          {pattern && <Canvas pattern={pattern} />}
        </div>
      </div>
      {error && <ErrorModal message={error} />}
    </div>
  );
};

export default App;
